import React from "react";
import { NavigationContainer, DarkTheme } from "@react-navigation/native";
import { createStackNavigator } from "@react-navigation/stack";
import { createDrawerNavigator } from "@react-navigation/drawer";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createMaterialBottomTabNavigator } from "@react-navigation/material-bottom-tabs";
import { Icon } from "react-native-vector-icons/MaterialCommunityIcons";
import { Title } from "react-native-paper";

//Screens
import Hooks from "./Hooks";

const Stack = createStackNavigator();
const Drawer = createDrawerNavigator();
const MaterialBottomTabs = createMaterialBottomTabNavigator();
const BottomTab = createBottomTabNavigator();

export default function App() {
  //colocar um dark mode no app; falta um arquivo style que seja "claro"
  const MyTheme = {
    dark: false,
    colors: {
      primary: "white",
      background: "white",
      card: "#65509f",
      text: "white",
      border: "green",
    },
  };

  const homeStack = () => (
    <Stack.Navigator>
      {/* <Stack.Screen name="loginStack" component={this.loginStack} 
      options={{
        headerShown:false
      }} /> */}
      <Stack.Screen name="Home" children={homeDrawer} />
    </Stack.Navigator>
  );

  const loginStack = () => (
    <Stack.Navigator>
      <Stack.Screen
        name="Login"
        component={Login}
        options={{
          title: "LoginScreen",
        }}
      />
      <Stack.Screen
        name="SignUp"
        component={SignUp}
        options={{
          title: "SignUpScreen",
        }}
      />
      <Stack.Screen
        name="Home"
        component={bottomTab}
        options={{
          headerShown: false,
        }}
      />
      {/* <Stack.Screen name="Login with Google" component={ApiGoogle} /> */}
    </Stack.Navigator>
  );

  const homeDrawer = () => (
    <Drawer.Navigator
      options={{
        headerShown: false,
      }}
    >
      <Drawer.Screen name="Home" component={Home} />
      <Drawer.Screen name="Profile" component={Profile} />
      <Drawer.Screen name="IMC" component={Imc} />
    </Drawer.Navigator>
  );

  const bottomTab = () => {
    return (
      <BottomTab.Navigator
        options={{
          headerShown: false,
        }}
      >
        <BottomTab.Screen name="Home" component={homeStack} />
        <BottomTab.Screen name="Novo" component={NewTrain} />
        <BottomTab.Screen name="Perfil" component={Profile} />
      </BottomTab.Navigator>
    );
  };

  //botÃ£o bonito e animado. Tenho que arrumar isso
  /* mBottomTabs = () => {
    return <MaterialBottomTabs.Navigator>
      <MaterialBottomTabs.Screen
        name="Home" component={Home} 
        options={{
          tabBarLabel: "Home",
          tabBarIcon: () =>(
            <Icon style={[{ color: 'white' }]} size={25} name={'home'}/>
          ),
        }} />
      <MaterialBottomTabs.Screen
        name="IMC" component={Imc} 
        options={{
          tabBarLabel: "IMC",
          tabBarIcon: () =>(
            <Icon style={[{ color: 'white' }]} size={25} name={'weight'}/>
          ),
        }} />
    </MaterialBottomTabs.Navigator>
  } */

  return <NavigationContainer>{loginStack()}</NavigationContainer>;
}
